import { useForm } from "react-hook-form";
import { yupResolver } from '@hookform/resolvers/yup';
import * as yup from "yup";
import { api } from '../../services/api';
import { Link, useNavigate } from "react-router-dom";

import {Layout, StyledInput, SpanForgot } from "./style";


import { Button } from "../../components/Button/index";
import { Input } from "../../components/Input/index";
import { IFormData } from "./types.js";

const schema = yup.object({
  email: yup.string().email('email não é valido').required('Campo obrigatório'),
  password: yup.string().min(3,'No minimo 3 caracteres').required('Campo obrigatório'),
}).required();

const Login = () => {  
  const navigate =  useNavigate();

  const { control, handleSubmit, formState: { errors } } = useForm<IFormData>({
    resolver: yupResolver(schema),
    mode: 'onChange',
  });
  
  const onSubmit = async (formData: IFormData) => {
    try {
      const { data } = await api.get(`users?email=${formData.email}&senha=${formData.password}`)
      if(data.length === 1) {
        navigate('/feed')
      } else {
        alert('Email ou senha invalidos')
      }
    } catch {
      alert('Houve um erro, tente novamente!')
    }
  };
  
  return (
    <Layout>
    <h1>Entrar no Safe<span>Car</span></h1>
    <form onSubmit={handleSubmit(onSubmit)}>
      <StyledInput>
        <Input name='email' errorMessage={errors?.email?.message} control={control}  placeholder="E-mail" />
        <Input name='password' errorMessage={errors?.password?.message} control={control}  placeholder="Senha"/>
      </StyledInput>
        <Button type="submit" title="Entrar" variant="secondary" /> 
    </form>
      <Link style={{ textDecoration: 'none' }} to="/register"><SpanForgot>Cadastrar-se</SpanForgot></Link>
      <Link style={{ textDecoration: 'none' }} to="/recover"><SpanForgot>Esqueci minha senha</SpanForgot></Link>
      </Layout>
  )
}

export { Login };
