import { useState, ChangeEvent, useEffect } from 'react';
import { Link, useNavigate, useParams } from 'react-router-dom';
import { Layout, BodyLayout, StyledInput } from './style';
import apiLogin from '../../services/api';
import { Button, Form } from 'react-bootstrap';

interface IUser{  
  name: string;
  senha: string;
  email: string;
}

const Cadastro: React.FC = () => {

    const navigate = useNavigate()
    const { id } = useParams<{ id: string }>()

    const [model, setModel] = useState<IUser>({
        name: '',
        senha: '',
        email: '',
    })
    
    useEffect(() => {
        console.log("Valor de Id"+id)
        if (id != undefined) {
            findUser(id)
        }
    }, [id])

    function updatedModel(e: ChangeEvent<HTMLInputElement>) {
        setModel({
            ...model,
            [e.target.name]: e.target.value
        })
    }

    async function onSubmit(e: ChangeEvent<HTMLFormElement>){
        e.preventDefault()

        if (id != undefined) {
            const response = await apiLogin.put('/users/'+id, model)            
        }
        else{
            const response = await apiLogin.post('/users', model)
        }
        back()
    }

    function back(){
        navigate(-1);
    }

    async function findUser(id: string){
        const response = await apiLogin.get('/users/'+id)    
        console.log(response)
        setModel({
            name: response.data.name,
            senha: response.data.senha,
            email: response.data.email,
        })


  return (
    <>
     <BodyLayout>
    <Layout>
      <div className="container">
            <br />
            <div className="user-header">
                <h1>Editar Cliente {id} </h1>
                <Button variant="btn btn-outline-success" size="sm" onClick={back}>Voltar</Button>
            </div>
            <br />
            <div className="container">
                <Form onSubmit={onSubmit}>

                        <Form.Group>
                            <Form.Label>Nome</Form.Label>
                            <Form.Control
                                type="text"
                                name="name"
                                value={model.name}
                                onChange={(e: ChangeEvent<HTMLInputElement>) => updatedModel(e)}/>
                        </Form.Group>


                    <Form.Group>
                        <Form.Label>Senha</Form.Label>
                        <Form.Control
                            type="text"
                            name="senha"
                            value={model.senha}
                            onChange={(e: ChangeEvent<HTMLInputElement>) => updatedModel(e)}/>
                    </Form.Group>

                    <Form.Group>
                        <Form.Label>Email</Form.Label>
                        <Form.Control
                            type="text"
                            name="email"
                            value={model.email}
                            onChange={(e: ChangeEvent<HTMLInputElement>) => updatedModel(e)}/>
                    </Form.Group>
                    <br/>
                    <Button variant="btn btn-outline-success" type="submit">
                        Salvar
                    </Button>
                </Form>
            </div>
        </div>
        </Layout>
        </BodyLayout>
    </>
    );
  }
}
export default Cadastro;
