import React, { useState, useEffect } from 'react';
import { Table, Button } from 'react-bootstrap';
import { Navigate, useNavigate } from 'react-router-dom';
import api from '../../services/api';
import moment from 'moment';


interface IUsers{

    id: number;
    placa: string;
    name: string;
    status: string;
    data: string;   
    modelo: string;
    pagamento: string;

}

const Users: React.FC = () => {

    const [users, setUsers] = useState<IUsers[]>([])
    const navigate = useNavigate()

    useEffect(() => {
        loadUsers()

}, [])

async function loadUsers() {
    const response = await api.get('/users')
    console.log(response);
    setUsers(response.data)

}

function formatDate(date: Date){
    return moment(date).format('DD/MM/YYYY')
}

function newTask(){
    navigate("/users_cadastro")
}

function editTask(id: number){        
    navigate("/Registros"+id)
    loadUsers()
}

async function deleteTask(id: number){
    await api.delete("/users/"+id)
    loadUsers()
}

return (

<div className="container">
    <br />
    <div className="user-header">
    <h1>Página de Usuários</h1>
    <Button variant="dark" size="sm" onClick= { newTask } >Novo Usuário</Button>
</div>
    <br />
    <Table striped bordered hover className="text-center">
        <thead>
            <tr>
            <th>id</th>
            <th>Nº DA PLACA</th>
            <th>NOME</th>             
            <th>STATUS</th>
            <th>DATA</th>
            <th>MODELO</th>
            <th>PAGAMENTO</th>
            </tr>
        </thead>
        <tbody>
            {
                users.map(user => (
                <tr key={user.id}>
                <td>{user.id}</td>
                <td>{user.placa}</td>
                <td>{user.name}</td> 
                <td>{user.status}</td>                
                <td>{user.data}</td>
                <td>{user.modelo}</td>
                <td>{user.pagamento}</td>
                <td>
                <Button size="sm" variant="primary" onClick={() => editTask(user.id)}>Editar</Button>{' '}
                <Button size="sm" variant="success" onClick={() => (user.id)}>Finalizar</Button>{' '}
                <Button size="sm" variant="warning" onClick={() => (user.id)}>Visualizar</Button>{' '}
                <Button size="sm" variant="danger" onClick={() => deleteTask(user.id)}>Remover</Button>{' '} 
                </td>
                </tr>
            ))
        }
    </tbody>
    </Table>
</div>
    );

}

export default Users;
